mapper.insert:
INSERT INTO MYBOARD (	SEQID
						,TITLE
						,PASS
						,WRITER
						,CONTENT
						,HITS
						,RDATE
						,UDATE )

			VALUES (	myboard_seq.nextval
						,#{title}
						,#{pass}
						,#{writer}
						,#{content}
						,#{hits}
						,SYSDATE
						,'' )

mapper.update:
UPDATE	REBOARD SET
					CMMT   = #{cmmt}
					,WRITER = #{writer}
					,STAR   = #{star}
				WHERE
					SEQID = #{seqid}

UPDATE MYBOARD 	SET	TITLE =#{title}
					,WRITER =#{writer}
					,CONTENT =#{content}
					,UDATE = sysdate
				WHERE
					SEQID = #{seqid}

mapper.select:
SELECT COUNT(*) FROM MYBOARD
				WHERE	SEQID =#{seqid}
				AND 	PASS =#{pass}
